con:config.script:
//Used to invoke printing in console
log.info(' This is an initial Test Script ');

//context is used to access variables scoped to a testcase
//Hence context scope is fixed to test case
//Syntax for fetching context variables context.expand('${#TestCase#initialValue}');
//Single quotes are mandatory
// we will log it to console to see the value
log.info ('Value of Test case level variable initialValue is : '+context.expand('${#TestCase#initialValue}'));

// testRunner is used to fetch variables across projecct.
//We can fetch variables from testSuite, Project, TestCase every thing
//But you need to follow Hirerachy while fecthing details 
// Project >> TestSuite >> TestCase >> TestStep
//We will try to fetch a property set in Test case add

// Initially we will declare testRunner then we have to state current location  //testRunner.testCase
//Since we have to get property from another testCase we need to access it parent element testSuite // testRunner.testCase.testSuite
//From testSuite get hold of the testCases array and get testCase by its name  //testRunner.testCase.testSuite.testCases["add"]
//Finally fetch the property by invoking getPropertyValue like getPropertyValue("addProperty001")
//Final Expression is mentioned below
testRunner.testCase.testSuite.testCases['add'].getPropertyValue('addProperty001');

//Setting property value
testRunner.testCase.testSuite.testCases['add'].setPropertyValue('setFromScript','IamFromScript');

//Getting Request value of teststep
testRunner.testCase.testSuite.testCases['add'].testSteps['Add'].getPropertyValue('Request');

//Fetching project level properties
def project = testRunner.testCase.testSuite.project;
project.setPropertyValue('author','dexter');
project.getPropertyValue('author');

//Parsing xml
import com.eviware.soapui.support.XmlHolder;
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext;

def addTestStep = testRunner.testCase.testSuite.testCases['add'].testSteps['Add']; // Fetch Add Teststep
def addXmlReqContent = addTestStep.getPropertyValue('Request');
def addRequest = new XmlHolder(addXmlReqContent); // Adding xml content to xml request
addRequest.setNodeValue('//tem:Add/tem:intA','49'); //Setting Node Values 
addRequest.setNodeValue('//tem:Add/tem:intB','51');
def finalAddRequestXml = addRequest.getXml();
testRunner.testCase.testSuite.testCases['add'].testSteps['Add'].setPropertyValue('Request',finalAddRequestXml);
def contextAddTestStep = new WsdlTestRunContext(addTestStep);
addTestStep.run(testRunner,contextAddTestStep );

 import com.eviware.soapui.support.XmlHolder;
 import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext;
 //fetching Test Steps
 def addTestStep = testRunner.testCase.testSuite.testCases['add'].testSteps['Add']; 
 def subTestStep = testRunner.testCase.testSuite.testCases['add'].testSteps['Sub']; 
 def mulTestStep = testRunner.testCase.testSuite.testCases['add'].testSteps['Mul']; 
 def divTestStep = testRunner.testCase.testSuite.testCases['add'].testSteps['Div']; 

 //Fetching Requests
 def addXmlReqContent = addTestStep.getPropertyValue('Request');
 def subXmlReqContent = subTestStep.getPropertyValue('Request');
 def mulXmlReqContent = mulTestStep.getPropertyValue('Request');
 def divXmlReqContent = divTestStep.getPropertyValue('Request');

 //Fetching request xmls
 def addRequest = new XmlHolder(addXmlReqContent); 
 def subRequest = new XmlHolder(subXmlReqContent); 
 def mulRequest = new XmlHolder(mulXmlReqContent); 
 def divRequest = new XmlHolder(divXmlReqContent); 

//***************************************Addition End
 //Setting Add req parameters
 addRequest.setNodeValue('//tem:Add/tem:intA','49');
 addRequest.setNodeValue('//tem:Add/tem:intB','51');

 //Fetching add final xml
 def finalAddRequestXml = addRequest.getXml();

 //Setting Request property
 testRunner.testCase.testSuite.testCases['add'].testSteps['Add'].setPropertyValue('Request',finalAddRequestXml);

 //Fetching Context for step
 def contextAddTestStep = new WsdlTestRunContext(addTestStep);

 //Executing Add Service
 addTestStep.run(testRunner,contextAddTestStep );

 //Fetching Add Response
 def addResponseContent =  addTestStep.getPropertyValue('Response');
 def addResponse = new XmlHolder(addResponseContent);
 addResponse.namespaces["ns"] = "http://tempuri.org/";
 def addResult = addResponse.getNodeValue('//ns:AddResponse/ns:AddResult');

//Verifying if result is 100
assert addResult == '100' ;

if('100'.equalsIgnoreCase(addResult)){
	log.info ' Addition Assertion Sucessful ';
}
//***************************************Addition End
//***************************************Substraction Start
//Setting response value in Sub Request
subRequest.setNodeValue('//tem:Subtract/tem:intA',addResult);
subRequest.setNodeValue('//tem:Subtract/tem:intB','50');

def finalSubRequestXml = subRequest.getXml();
 //Setting SUbRequest property
 testRunner.testCase.testSuite.testCases['add'].testSteps['Sub'].setPropertyValue('Request',finalSubRequestXml);

 //Fetching Context for Substep
 def contextSubTestStep = new WsdlTestRunContext(subTestStep);

 //Executing Sub request
 subTestStep.run(testRunner, contextSubTestStep);

//Fetching Substraction Response
 def subResponseContent =  subTestStep.getPropertyValue('Response');
 def subResponse = new XmlHolder(subResponseContent);
 subResponse.namespaces['ns'] = 'http://tempuri.org/';
 def subResult = subResponse.getNodeValue('//ns:SubtractResponse/ns:SubtractResult');

//Assert will check if the result is same else it will stop execution of script
assert subResult == '50';

//Validating if subresult is 50
if('50'.equalsIgnoreCase( subResult )){
log.info ' Substraction Assertion Sucessful ';
}
//***************************************Substraction End
//***************************************Division Start
//fetching div request
divRequest.setNodeValue('//tem:Divide/tem:intA',subResult);
divRequest.setNodeValue('//tem:Divide/tem:intB','10');

def finalDivRequestXml = divRequest.getXml();
 //Setting DivRequest property
 testRunner.testCase.testSuite.testCases['add'].testSteps['Div'].setPropertyValue('Request',finalDivRequestXml);

 //Fetching Context for Divstep
 def contextDivTestStep = new WsdlTestRunContext(divTestStep);

 //Executing Div request
 subTestStep.run(testRunner, contextDivTestStep);

//Fetching Division Response
 def divResponseContent =  divTestStep.getPropertyValue('Response');
 def divResponse = new XmlHolder(divResponseContent);
 divResponse.namespaces['ns'] = 'http://tempuri.org/';
 def divResult = divResponse.getNodeValue('//ns:DivideResponse/ns:DivideResult');

//Assert will check if the result is same else it will stop execution of script
assert divResult == '5';

//Validating if Divisionresult is 5
if('5'.equalsIgnoreCase( divResult )){
log.info ' Division Assertion Sucessful ';
}
//***************************************Division End
//***************************************Muliplication Start
//fetching multiplication request
mulRequest.setNodeValue('//tem:Multiply/tem:intA',divResult);
mulRequest.setNodeValue('//tem:Multiply/tem:intB','20');

def finalMulRequestXml = mulRequest.getXml();
 //Setting MultiplicationRequest property
 testRunner.testCase.testSuite.testCases['add'].testSteps['Mul'].setPropertyValue('Request',finalMulRequestXml);

 //Fetching Context for Divstep
 def contextMulTestStep = new WsdlTestRunContext(mulTestStep);

 //Executing Div request
 subTestStep.run(testRunner, contextMulTestStep);

//Fetching Division Response
 def mulResponseContent =  mulTestStep.getPropertyValue('Response');
 def mulResponse = new XmlHolder(mulResponseContent);
 mulResponse.namespaces['ns'] = 'http://tempuri.org/';
 def mulResult = mulResponse.getNodeValue('//ns:MultiplyResponse/ns:MultiplyResult');

//Assert will check if the result is same else it will stop execution of script
assert mulResult == '100';

//Validating if multiplicationresult is 100
if('100'.equalsIgnoreCase( mulResult )){
log.info ' Multiplication Assertion Sucessful ';
}
//***************************************Multiplication End
